<!DOCTYPE html><html><head>	<meta charset="utf-8" />	<meta name="viewport" content="maximum-scale=1.0,minimum-scale=1.0,user-scalable=0,width=device-width,initial-scale=1.0" />	<title>送货单</title>	<link rel="stylesheet" href="https://cdn.bootcss.com/weui/1.1.3/style/weui.min.css" />	<link rel="stylesheet" href="https://cdn.bootcss.com/jquery-weui/1.2.1/css/jquery-weui.min.css" />	<script src="https://cdn.bootcss.com/jquery/1.11.0/jquery.min.js"></script>	<script src="https://cdn.bootcss.com/jquery-weui/1.2.1/js/jquery-weui.min.js"></script>	<script src="https://cdn.bootcss.com/jquery-weui/1.2.1/js/swiper.min.js"></script></head><body style="background:#f3f3f3">	@foreach ($data['val'] as $item)	<div class="weui-panel weui-panel_access">		<div class="weui-panel__hd">送货单编号：{{$item['no']}}</div>		<div class="weui-panel__hd">创建时间：{{$item['created_at']}}</div>		<div class="weui-panel__hd">送货方式：@if ($item['distribution_mode'] == '0') 快递送货			<p class="weui-media-box__desc">快递公司：{{$item['express_name']}}&nbsp;&nbsp;快递单号：{{$item['express_no']}} @elseif ($item['distribution_mode'] == '1') 送货 @elseif ($item['distribution_mode'] == '2') 客户自提 @endif</p>		</div>		<div class="weui-panel__bd">			@foreach ($item['parts'] as $pi)			<div class="weui-media-box weui-media-box_appmsg">				<div class="weui-media-box__hd">					<img class="weui-media-box__thumb" src="{{ $pi['diagram'] }}" alt="" />				</div>				<div class="weui-media-box__bd">					<h4 class="weui-media-box__title">{{ $pi['name'] }}</h4>					<p class="weui-media-box__desc">总数量：{{ $pi['product_num'] }}</p>					<p class="weui-media-box__desc">送货数量:{{ $pi['yscount'] }}</p>					<p class="weui-media-box__desc">零件状态:@if ($pi['status'] &lt;= 3) 已分批送货 @elseif ($pi['status'] == 4) 送货完成 @else 完成 @endif</p>				</div>			</div>			@endforeach		</div>		<div class="weui-panel__hd">送货单状态： @if ($item['status'] == '0') 已发货 @elseif ($item['status'] == '1') 已收货 @else 完成 @endif</div>		<div class="weui-panel__ft">			<div class="button_sp_area" style="padding-bottom: 10px;padding-left:10px">@if ($item['status'] == '0')				<a href="javascript:;" data-cfurl="{{$item['confirm']}}" class="weui-btn weui-btn_mini weui-btn_primary">确认收货</a>@endif				<a href="javascript:;" data-excode="{{$item['express_code']}}" data-exno="{{$item['express_no']}}" class="weui-btn weui-btn_mini weui-btn_default ">查看物流</a>			</div>		</div>	</div>	@endforeach	<!-- body 最后 -->	<style>		.weui-dialog__bd {			max-height: 300px;			overflow-y: scroll;			text-align: left;		}		.weui-btn_default {			margin-top: 15px;		}	</style>	<script src="https://res.wx.qq.com/open/js/jweixin-1.4.0.js" type="text/javascript" charset="utf-8"></script>	<script type="text/javascript" charset="utf-8">		wx.config(@json($data["jssdk"]));		$("#dialogsFalse1").click(function() {			wx.closeWindow()		});		$(".weui-btn_primary").click(function() {			var url = "";			$.confirm({				title: "确认收货？",				text: "请确认您是否已经收到货物",				onOK: function() {					$.ajax({						type: "POST",						url: url,						data: {							val: $(this).data()						},						dataType: "json",						success: function(data) {							$.toast(data['message']);						},						error: function(xhr, type, error) {							$.toast("错误，请重试", "cancel");						}					})				},			})		});		$(".weui-btn_default").click(function() {			$.showLoading();			var url = "";			$.ajax({				type: "POST",				url: url,				data: {					shipperCode: $(this).data("excode"),					logisticCode: $(this).data("exno")				},				dataType: "json",				success: function(data) {					$.hideLoading();					var outex = "";					if (data == "" || data == "无物流信息") {						outex = "暂无物流信息"					} else {						$.each(data,						function(i, n) {							outex = (n["AcceptTime"] + "<br/>" + n["AcceptStation"] + "<br/><br/>") + outex						})					}					$.alert({						title: "物流信息",						text: outex,					})				},				error: function(xhr, type, error) {					$.hideLoading();					alert("发生错误，请刷新重试。")				}			})		});		$(".weui-media-box__thumb").click(function() {			var bp = $.photoBrowser({				items: [$(this).attr("src")]			});			bp.open()		});	</script></body></html>